const items = $input.all();

return items.flatMap(item => {
  if (!item.json.response || item.json.response.length === 0) return [];

  const league_id = parseInt(item.json.parameters?.league);
  const season = parseInt(item.json.parameters?.season);

  return item.json.response.map(fixture => ({
    json: {
      external_id: fixture.fixture.id,
      league_id,
      season,
      home_team_external_id: fixture.teams.home.id,
      away_team_external_id: fixture.teams.away.id,
      match_date: fixture.fixture.date,
      round: fixture.league.round,
      status: fixture.fixture.status.short === 'FT' ? 'finished' :
              ['1H','2H','HT','ET','P'].includes(fixture.fixture.status.short) ? 'live' : 'scheduled',
      score_home: fixture.goals.home ?? 0,
      score_away: fixture.goals.away ?? 0
    }
  }));
});
